import React, {Component, useState} from "react";
import {connect} from "react-redux";
import Messages from "./Messages";
import "./MessageList.css";
import {addMessage} from "../actions";

class MessageList extends Component {

    //TODO use different method for resolving 'this' keyword
    constructor(props) {
        super(props);

        //Use reducer in redux
        this.state = {
            items: []
        };

        this.addItem = this.addItem.bind(this);
        //this.deleteItem = this.deleteItem.bind(this);
    }

    addItem(e) {
        if (this._inputElement.value !== "") {
            var newItem = {
                text: this._inputElement.value,
                key: Date.now()
            };

            this.setState((prevState) => {
                return {
                    items: prevState.items.concat(newItem)
                };
            });

            this._inputElement.value = "";
        }

        console.log(this.state.items);

        e.preventDefault();
    }

    // deleteItem(key) {
    //     var filteredItems = this.props.items.filter(function (item) {
    //         return (item.key !== key);
    //     });
    //
    //     this.setState({
    //         items: filteredItems
    //     });
    // }

    render() {
        return (
            <div className="messageListMain">
                <div className="header">
                    <form onSubmit={this.addItem}>
                        <input ref={(a) => this._inputElement = a}>
                        </input>
                        <button type="submit">add</button>
                    </form>
                </div>
                <Messages entries={this.props.items}
                          delete={this.deleteItem}/>
            </div>
        );
    }
}

const mapStateToProps = (state) => { //name is by convention
    //state has entire state of app!!
    return { items: state.items }; //now it will appear as props
};


export default connect(mapStateToProps, {addMessage})(MessageList);
