import { combineReducers } from 'redux';

// const initialState = {
//     list:[]
// };

const messageReducer = (list = [], action) => {
    if (action.type === 'ADD_MESSAGE') {
        return list.append(action.payload);
    }
    return list;
};

export default combineReducers({
    list: messageReducer(),
});
