import React, {Component} from "react";

class Detail extends Component {

    constructor(props) {
        super(props);
        this.state = {
            text : this.props.dialogText
        }
    }

    componentWillReceiveProps(nextProps) {
        this.setState({text: nextProps.dialogText})
    }

    render() {
        var detailText = this.state.text;

        return (
            <div className="theDialog">
                <h1>
                    {detailText}
                </h1>
                <p>
                    {detailText}
                </p>
            </div>
        );
    }
}
export default Detail;
