import React, {Component} from "react";
import Detail from "./Detail";

class Messages extends Component {

    constructor(props) {
        super(props);

        this.createMessage = this.createMessage.bind(this);
        //this.createDetail = this.createDetail.bind(this);
    }

    createMessage(item) {
        //return <li onClick={() => this.createDialog(item.text)} key={item.key}>{item.text}</li>
        return <li key={item.key}>{item.text}</li>
    }

    render() {
        var messageEntries = this.props.entries;
        var listItems = messageEntries.map(this.createMessage);

        return (
            <div>
                <ul className="theList">
                    {listItems}
                </ul>
                <Detail/>
            </div>
        );
    }
};

export default Messages;
