const apiUrl = '/express_backend';


export const addMessage = message => {
    return {
        type: 'ADD_MESSAGE',
        payload: message,
    };
};

export const deleteMessage = messages => {
    return {
        type: 'DELETE_MESSAGE',
        payload: messages,
    };
};

export const createMessage = message => {
    return (dispatch) => {
        return fetch(apiUrl, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(message)
        }).then(response => {
            response.json()
                .then(messages => {
                    dispatch(addMessages(messages))
                })
                .catch(error => {
                    throw(error);
                })
        })
            .catch(error => {
                throw(error);
            });
    };
};

export const addMessages = message => {
    return {
        type: 'ADD_MESSAGES',
        payload: message,
    };
};
