import { ADD_MESSAGE, DELETE_MESSAGE, FETCH_MESSAGES } from './types';

const apiUrl = '/express_backend';


export const addMessage = message => {
    return {
        type: 'ADD_MESSAGE',
        payload: message,
    };
};

export const deleteMessage = messages => {
    return {
        type: 'DELETE_MESSAGE',
        payload: messages,
    };
};

export const createMessage = message => {
    return (dispatch) => {
        return fetch('${apiUrl}', {method: 'POST'})
            .then(response => {
                dispatch(createPostSuccess(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };
};

export const createMessageSuccess =  (data) => {
    return {
        type: ADD_POST,
        payload: {
            _id: data._id,
            title: data.title,
            body: data.body
        }
    }
};
