import {FETCH_GITHUB_DATA} from "./types";

const apiUrl = "/express_backend";


export const fetchData = (data) => {
    return {
        type: FETCH_GITHUB_DATA,
        data
    }
};

export const fetchGithubData = () => {
    return (dispatch) => {
        return fetch(apiUrl)
            .then(response => {
                dispatch(fetchData(response.data))
            })
            .catch(error => {
                throw(error);
            });
    };
};

export const addMessage = message => {
    return {
        type: 'ADD_MESSAGE',
        payload: message,
    };
};

export const deleteMessage = messages => {
    return {
        type: 'DELETE_MESSAGE',
        payload: messages,
    };
};
