import {combineReducers} from 'redux';

// const initialState = {
//     items: [{key:1, text:'hello'}, {key:2, text:'i am'}, {key:3, text:'content'}]
// };

const messageReducer = (items = [{key:1, text:'hello'}, {key:2, text:'i am'}, {key:3, text:'content'}], action) => {
    if (action.type === 'ADD_MESSAGE') {
        return items.concat(action.payload);
    }
    return items;
};

export default combineReducers({
    //spent literally hours what went wrong. CHANGED messageReducer() to messageReducer and voila
    items: messageReducer,
});
