import React, {Component} from "react";
import Detail from "./Detail";

class Messages extends Component {

    constructor(props) {
        super(props);

        this.state = {
            dialogText: {}
        };

        this.createMessage = this.createMessage.bind(this);
        this.updateDialog = this.updateDialog.bind(this);
    }

    createMessage(item) {
        return <li onClick={() => this.updateDialog(item.text)} key={item.key}>{item.text}</li>
    }


    updateDialog(text) {
        this.setState({
            dialogText: {text}
        });
    }

    render() {
        var messageEntries = this.props.entries;
        var listItems = messageEntries.map(this.createMessage);

        return (
            <div>
                <ul className="theList">
                    {listItems}
                </ul>
                <Detail text={this.state.dialogText}/>
            </div>
        );
    }
};

export default Messages;
