import React, {Component} from "react";

class Detail extends Component {

    constructor(props) {
        super(props);
        this.setState({
            text: this.props.dialogText
        })
    }


    render() {
        var detailText = this.state.text;

        return (
            <div className="theDialog">
                <h1>
                    {this.state.text}
                </h1>
                <p>
                    {this.state.text}
                </p>
            </div>
        );
    }
};

export default Detail;
