import { combineReducers } from 'redux';

// const initialState = {
//     list:[]
// };

const messageReducer = (items = [], action) => {
    if (action.type === 'ADD_MESSAGE') {
        return items.append(action.payload);
    }
    return items;
};

export default combineReducers({
    items: messageReducer(),
});
